// Retrieve the list of vendors
const vendedores = $node["Vendedores"].json.vendedores;
const numVendors = vendedores.length;

// Log the vendors and the number of vendors
console.log("Vendedores List:", vendedores);
console.log("Number of Vendors:", numVendors);

// Get the lead index from the Wait node dynamically
const leadIndex = $node["Wait"].json.leadIndex;
console.log("Lead Index from Wait Node:", leadIndex); // Log the lead index to check if it's correctly retrieved

// Round-robin logic to assign a vendor
const vendedorIndex = leadIndex % numVendors;
console.log("Calculated Vendor Index:", vendedorIndex); // Log the calculated vendor index

const assignedVendedor = vendedores[vendedorIndex]; // Select the vendor based on the index
console.log("Assigned Vendedor:", assignedVendedor); // Log the assigned vendor

// Return the lead with the assigned vendor
return {
  json: {
    ...$json, // Include all original lead data
    assignedVendedor // Add the assigned vendor to the output
  }
};

const indexedLeads = $input.all().map((item, i) => {
    return {
        json: {
            ...item.json,
            leadIndex: i // Assign a unique index to each lead
        }
    };
});

return indexedLeads;

function generateUUID() {
  let dt = new Date().getTime();
  let uuid = 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    let r = (dt + Math.random() * 16) % 16 | 0;
    dt = Math.floor(dt / 16);
    return (c == 'x' ? r : (r & 0x3 | 0x8)).toString(16);
  });
  return uuid;
}

// Gera e retorna o UUID
return {
  uuid: generateUUID()
};

