// Check if the current item came from the error branch
const isError = $json.hasOwnProperty('error');

// Get the status code from the response (default to 0 if undefined)
const statusCode = $json.statusCode || 0;

// Determine success: any status code between 200 and 299 (inclusive) is considered a success
const isSuccess = statusCode >= 200 && statusCode < 300;

// Prepare the log entry
const logEntry = {
    uuid: uuid,  // Use the inline-generated UUID
    leadId: $node["Wait"]?.json?.leadid || null,  // Retrieve lead ID from 'Wait' node
    ownerId: $node["Roleta"]?.json?.assignedVendedor?.id || null,
    ownerName: $node["Roleta"]?.json?.assignedVendedor?.name || null,
    status: isSuccess ? 'success' : 'failure',
    message: isError ? $json.error.message : (isSuccess ? 'Successfully updated CRM' : `Failed with status ${statusCode}`),
    timestamp: new Date().toISOString()  // Full ISO 8601 timestamp
};

// Return the normalized log entry
return {
    json: logEntry
};
